    public View onCreateView(@NonNull LayoutInflater inflater,
            ViewGroup container, Bundle savedInstanceState) {
        homeViewModel =
                new ViewModelProvider(this).get(HomeViewModel.class);
        View root = inflater.inflate(R.layout.fragment_home, container, false);

        cardView = (CardView) root.findViewById(R.id.cardViewLista);
        recyclerViewCanciones = (RecyclerView) root.findViewById(R.id.RecyclerViewNotas);
        recyclerViewCanciones.setLayoutManager(new LinearLayoutManager(recyclerViewCanciones.getContext()));


        //final TextView textView = root.findViewById(R.id.text_home); Lo que mandaba al main
        homeViewModel.getText().observe(getViewLifecycleOwner(), new Observer<String>() {
            @Override
            public void onChanged(@Nullable String s) {
               //textView.setText(s); Lo mandaba a la activity
                enviarDatos();
            }
        });
        return root;
    }

